# Importing modules/packagesimport csvimport osimport pprint as pp# Define the csvpath variable using os.path.join methodcsvpath = os.path.join("resources", "budget_data.csv")# Use context manager to open and read the budget_data.csv filewith open(csvpath, "r") as budget_data:    # Reads the data from the budget_data.csv via the csv.reader method into csv_reader variable    csv_reader = csv.reader(budget_data, delimiter = ",")    csv_header = next(csv_reader)        # Empty lists and variables:    total_months = 0    total = 0    diff_amt_list = []    diff_amt = 0    avg_change = 0    greatest_increase_profit = []    greatest_decrease_profit = []        # The list comprehension reads each row of csv_reader object and appends to budget_data_list list    budget_data_list = [row for row in csv_reader]    # Finds total months based on length of budget_data_list list    total_months = len(budget_data_list)        # For-loop to go through budget_data_list list     for i in range(0, len(budget_data_list)):        # pp.pprint(budget_data_list[i])                # Sums up all the profit/losses values in list        total += int(budget_data_list[i][1])                #         for j in range(0, (len(budget_data_list[i]) - 1)):            # print(budget_data_list[i][1])            if i != 85:                diff_amt = int(budget_data_list[i+1][1]) - int(budget_data_list[i][1])                diff_amt_list.append(diff_amt)                # print(i, diff_amt)            elif i == 85:                break        # List comprehension that reads through the budget_data_list list and calculates the profit/losses    diff_amt_list = [(int(budget_data_list[i+1][1]) - int(budget_data_list[i][1])) if i != 85 else i == 85 for i in range(0, len(budget_data_list)) for j in range(0, (len(budget_data_list[i]) - 1)) ]        print(max(diff_amt_list))    print(min(diff_amt_list))        for i in range(0, len(budget_data_list)):        for j in range(0, (len(budget_data_list[i]) - 1)):            # print(budget_data_list[i][1])            if budget_data_list[i][1] == (str(max(diff_amt_list))):                greatest_increase_profit.append(budget_data_list[i][0])                greatest_increase_profit.append(budget_data_list[i][1])                # print(greatest_increase_profit)            elif budget_data_list[i][1] == (str(min(diff_amt_list))):                greatest_decrease_profit.append(budget_data_list[i])                avg_change = sum(diff_amt_list)/len(diff_amt_list)    # print(avg_change)        # if i == 0:            #     diff_amt = int(budget_data_list[i+1][1]) - int(budget_data_list[i][1])        # elif i == len(budget_data_list):        #     break                # avg_change += diff_amt        # avg_change /= total_months            print("""Financial Analysis\n----------------------------""")    print(f"Total Months: {total_months}")    print(f"Total: ${total}")    print(f"Average Change: {avg_change}")    print(f"Greatest Increase In Profits: {greatest_increase_profit}")    print(f"Greatest Decrease In Profits: {greatest_decrease_profit}")    