# Importing modules/packagesimport csvimport osimport pprint as pp# Define the csvpath variable using os.path.join methodcsvpath = os.path.join("resources", "election_data.csv")def candidates(voter_list):    candidate_list = []        for i in range(0, len(voter_list)):        if not voter_list[i][2] in candidate_list:            candidate_list.append(voter_list[i][2])        return candidate_listdef vote_counter(voter_list, total_votes):    candidate_list = []    candidates_only_list = []    candidate_votes = []        for i in range(0, len(voter_list)):        if not voter_list[i][2] in candidate_list:            candidate_list.append(voter_list[i][2])        for i in range(0, len(voter_list)):        candidates_only_list.append(voter_list[i][2])        candidate_1 = candidates_only_list.count(candidate_list[0])    candidate_2 = candidates_only_list.count(candidate_list[1])    candidate_3 = candidates_only_list.count(candidate_list[2])    candidate_4 = candidates_only_list.count(candidate_list[3])    percent_vote_1 = (candidate_1/total_votes) * 100    percent_vote_2 = (candidate_2/total_votes) * 100    percent_vote_3 = (candidate_3/total_votes) * 100    percent_vote_4 = (candidate_4/total_votes) * 100        candidate_votes = [[candidate_1, candidate_2, candidate_3, candidate_4],                        [percent_vote_1, percent_vote_2, percent_vote_3, percent_vote_4]]        return candidate_votes# Use context manager to open and read the election_data.csv filewith open(csvpath, "r") as election_data:    # Reads the data from the election_data.csv via the csv.reader method into csv_reader variable    csv_reader = csv.reader(election_data, delimiter = ",")    csv_header = next(csv_reader)        # Empty lists and variables    total_votes = 0    percentage_vote = 0    candidate_vote = 0    winner = ""        election_data_list = [voter for voter in csv_reader]        total_votes = len(election_data_list)        unique_candidate_list = candidates(election_data_list)        each_candidate_votes = vote_counter(election_data_list, total_votes)    print("""Election Results\n----------------------------""")    print(f"Total Votes: {total_votes}")    print("----------------------------")    print(f"{unique_candidate_list[0]}: {each_candidate_votes[1][0]:.3f}% ({each_candidate_votes[0][0]})")    print(f"{unique_candidate_list[1]}: {each_candidate_votes[1][1]:.3f}% ({each_candidate_votes[0][1]})")    print(f"{unique_candidate_list[2]}: {each_candidate_votes[1][2]:.3f}% ({each_candidate_votes[0][2]})")    print(f"{unique_candidate_list[3]}: {each_candidate_votes[1][3]:.3f}% ({each_candidate_votes[0][3]})")    print("----------------------------")    print(f"Winner: ")    print("----------------------------")    